mapper.select:

select 
	ad_id  adId,
	ad_name adName,
	(select dic_value from tsied_sys_dic where id = ad_status) adStatus,
	(select dic_value from tsied_sys_dic where id = ad_project) adProject,
	(select dic_value from tsied_sys_dic where id = ad_type) adType,
	ad_addr adAddr,
	ad_domain adDomain,
	(select dic_value from tsied_sys_dic where id = ad_social_software) adSocialSoftware,
	ad_cost_budget adCostBudget,
	ad_start_time adStartTime,
	ad_end_time adEndTime
from tsied_advert

   1 = 1
    
		and ad_id = #{adId}
	
    
	
		and ad_name = #{adName}
	
	
		and ad_status = #{adStatus}
	
	
		and ad_project = #{adProject}
	
	
		and ad_type = #{adType}
	
	
		and ad_addr = #{adAddr}
	
	
		and ad_social_software = #{adSocialSoftware}
	
	
		and ad_cost_budget = #{adCostBudget}
	
	
	
		 and ad_start_time>=#{adStartTime,jdbcType=TIMESTAMP} 
	
	
	
		and ad_start_time<=#{adStartEnd,jdbcType=TIMESTAMP} 
	
	
	
		and ad_end_time>=#{adEndTime,jdbcType=TIMESTAMP} 
	
	
	
	and ad_end_time<=#{adEndEnd,jdbcType=TIMESTAMP} 
	
	
	
	and ad_end_time<=#{adEndEnd,jdbcType=TIMESTAMP}


select 
	ad_id  adId,
	ad_name adName,
	(select dic_value from tsied_sys_dic where id = ad_status) adStatus,
	(select dic_value from tsied_sys_dic where id = ad_project) adProject,
	(select dic_value from tsied_sys_dic where id = ad_type) adType,
	ad_addr adAddr,
	ad_domain adDomain,
	(select dic_value from tsied_sys_dic where id = ad_social_software) adSocialSoftware,
	ad_cost_budget adCostBudget,
	ad_start_time adStartTime,
	ad_end_time adEndTime
from tsied_advert

    1 = 1
    
		and ad_id = #{adId}
	
    
	
		and ad_name = #{adName}
	
	
		and ad_status = #{adStatus}
	
	
		and ad_project = #{adProject}
	
	
		and ad_type = #{adType}
	
	
		and ad_addr = #{adAddr}
	
	
		and ad_social_software = #{adSocialSoftware}
	
	
		and ad_cost_budget = #{adCostBudget}
	
	
	
		 and ad_start_time>=#{adStartTime,jdbcType=TIMESTAMP} 
	
	
	
		and ad_start_time<=#{adStartEnd,jdbcType=TIMESTAMP} 
	
	
	
		and ad_end_time>=#{adEndTime,jdbcType=TIMESTAMP} 
	
	
	
	and ad_end_time<=#{adEndEnd,jdbcType=TIMESTAMP} 
	
	
	
	and ad_end_time<=#{adEndEnd,jdbcType=TIMESTAMP} 
			
	
	limit #{offset,jdbcType=TIMESTAMP},#{pageSize,jdbcType=TIMESTAMP};

select 
	ad_id adId,
	ad_name adName,
	ad_status adStatus,
	ad_project adProject,
	ad_type adType,
	ad_addr adAddr,
	ad_domain adDomain,
	ad_social_software adSocialSoftware,
	ad_cost_budget adCostBudget,
	ad_start_time adStartTime,
	ad_end_time adEndTime
from tsied_advert

    1 = 1
     
		and ad_id = #{adId}
	
	
		and ad_name = #{adName}
	
	
		and ad_status = #{adStatus}
	
	
		and ad_project = #{adProject}
	
	
		and ad_type = #{adType}
	
	
		and ad_addr = #{adAddr}
	
	
		and ad_social_software = #{adSocialSoftware}
	
	
		and ad_cost_budget = #{adCostBudget}
	
	
	
		     and DATE_FORMAT(ad_start_time, '%Y-%m-%d')>=  DATE_FORMAT(#{adStartTime}, '%Y-%m-%d')   
	
	
	
		   and DATE_FORMAT(ad_start_time, '%Y-%m-%d')<=  DATE_FORMAT(#{adStartEnd}, '%Y-%m-%d')   
	
	
	
		   and DATE_FORMAT(ad_end_time, '%Y-%m-%d')>=  DATE_FORMAT(#{adEndTime}, '%Y-%m-%d')   
	
	
	
		   and DATE_FORMAT(ad_end_time, '%Y-%m-%d')<=  DATE_FORMAT(#{adEndEnd}, '%Y-%m-%d')

mapper.update:
update tsied_advert set ad_name = #{adName}, ad_status = #{adStatus}, ad_project = #{adProject}, ad_type = #{adType}, ad_addr = #{adAddr}, ad_domain = #{adDomain}, ad_social_software = #{adSocialSoftware}, ad_cost_budget = #{adCostBudget}, ad_start_time = #{adStartTime,jdbcType=DATE}, ad_end_time = #{adEndTime,jdbcType=DATE}
1 = 1
and ad_id = #{adId}
